pageProps.query:
    query contentQuery($relativePath: String!) {
  ...LayoutQueryFragment
  page(relativePath: $relativePath) {
    ...PageParts
  }
}

    fragment LayoutQueryFragment on Query {
  global(relativePath: "index.json") {
    ...GlobalParts
  }
}

    fragment GlobalParts on Global {
  header {
    __typename
    name
    nav {
      __typename
      href
      label
    }
  }
  footer {
    __typename
    social {
      __typename
      facebook
      twitter
      instagram
      github
    }
  }
}


    fragment PageParts on Page {
  title
  hideTitle
  blocks {
    __typename
    ... on PageBlocksHero {
      pageTitle
      image {
        __typename
        src
        alt
      }
    }
    ... on PageBlocksContent {
      body
      color
    }
    ... on PageBlocksTestimonial {
      quote
      author
      color
    }
    ... on PageBlocksTeaser {
      photoSrc
      photoAlignment
      title
      description
      link
    }
    ... on PageBlocksHome {
      bio
      contact
    }
    ... on PageBlocksImprint {
      contact
      copyright
      privacy
    }
    ... on PageBlocksGallery {
      columns
      items {
        __typename
        image
        label
      }
    }
  }
}
