pageProps.query:
    query contentQuery($relativePath: String!) {
  ...LayoutQueryFragment
  page(relativePath: $relativePath) {
    ...PageParts
  }
}

    fragment LayoutQueryFragment on Query {
  global(relativePath: "index.json") {
    ...GlobalParts
  }
}

    fragment GlobalParts on Global {
  header {
    __typename
    name
    nav {
      __typename
      href
      label
    }
  }
  footer {
    __typename
    social {
      __typename
      facebook
      twitter
      instagram
      github
    }
  }
}


    fragment PageParts on Page {
  title
  hideTitle
  blocks {
    __typename
    ... on PageBlocksHero {
      tagline
      headline
      text
      actions {
        __typename
        label
        type
        icon
        link
      }
      image {
        __typename
        src
        alt
      }
      color
    }
    ... on PageBlocksFeatures {
      items {
        __typename
        icon {
          __typename
          name
          color
          style
        }
        title
        text
      }
      color
    }
    ... on PageBlocksContent {
      body
      color
    }
    ... on PageBlocksTestimonial {
      quote
      author
      color
    }
    ... on PageBlocksTeaser {
      photoSrc
      photoAlignment
      title
      description
      link
    }
    ... on PageBlocksHome {
      bio
      contact
    }
    ... on PageBlocksImprint {
      contact
      copyright
      privacy
    }
  }
}
